import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'app';
}
<body>
  <app-nav-menu></app-nav-menu>
  <header class="mb-5 header"></header>

  <main class="container">
    <router-outlet></router-outlet>
  </main>

  <footer class="pt-3 footer bg-dark text-light text-center mt-5">
    <div class="pt-5">
      <a href="/"><i class="ms-3 fa-lg fab fa-facebook float-lg"></i></a>
      <a href="/"><i class="ms-3 fa-lg fab fa-google-plus float-lg"></i></a>
    </div>
  </footer>
</body>
