import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FlightService } from '../api/services/flight.service';
import { FlightRm } from '../api/models/flight-rm';
import { AuthService } from '../auth/auth.service';
import { FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-book-flight',
  templateUrl: './book-flight.component.html',
  styleUrls: ['./book-flight.component.css']
})
export class BookFlightComponent {
  constructor(private route: ActivatedRoute,
    private router: Router,
    private flightService: FlightService,
    private authService: AuthService,
    private fb: FormBuilder) { }

  flightId: string = 'not loaded'
  flight: FlightRm = {}

  form = this.fb.group({
    number: [1]
  })

  ngOnInit(): void {
    if (!this.authService.currentUser) {
      this.router.navigate(['/register-passenger']);
    }

    this.route.paramMap
    .subscribe(p => this.findFlight(p.get("flightId")))
  }

  private findFlight = (flightId: string | null) => {
    this.flightId = flightId ?? 'not passed';

    this.flightService.findFlight({ id: this.flightId })
      .subscribe(flight => this.flight = flight, this.handleError);
  }

  private handleError = (err: any) => {
    if (err.status = 404) {
      alert("Flight not found!");
      this.router.navigate(['/search-flights']);

      console.log("Response status: " + err.status);
      console.log("Response status text: " + err.statusText);
      console.log(err);
    }
  }

  book = () => {
    console.log(`Booking ${this.form.get('number')?.value} passengers for the flight: ${this.flightId}`)
  }
}
<h2 class="text-center mb-4">Book Flight</h2>

<div class="card text-dark mt-5 text-center" style="background-color:#efefef">
  <div class="card-body">
    <div class="card-title">{{flight.airline}}</div>
    <div class="card-title" style="color:#ff3939">
      {{flight.remainingNumberOfSeats}} remaining seats
    </div>

    <div class="d-grid text-center g-2">
      <div class="row">
        <div class="col">

          <div class="col m-3">
            <i class="text-primary fas fa-plane-departure" style="font-size:20px"></i>
          </div>

          <div class="col">
            <i class="fa-solid fa-location-dot">
              {{flight.departure?.place}}
            </i>
          </div>

          <div class="col m-2">
            <i class="fa-regular"></i>
            {{flight.departure?.time | date}}
          </div>

          <div class="col m-2">
            <i class="fa-regular fa-clock"></i>
            {{flight.departure?.time | date:'shortTime'}}
          </div>
        </div>

        <div class="col">
          <div style="display:flex;">
            <i class="fa-solid fa-circle" style="margin-top:50px;font-size:9px;"></i>
            <hr style="display:block;width: 100%; margin-top:55px;" />
            <i class="fas fa-plane" style="margin-top:48px;"></i>
          </div>

          <i class="fas fa-usd">{{flight.price}}</i>
          <br />
          <form [formGroup]="form" (ngSubmit)="book()">
            <div class="col-6">
              <label>Number of seats</label>
              <input type="number" formControlName="number" value="" />
            </div>
            <div class="col-6">
              <button type="submit" class="btn btn-secondary btn-md mt-2">
                <i class="fas fa-ticket"> Book</i>
              </button>
            </div>
          </form>
          
        </div>

        <div class="col">
          <div class="col m-3">
            <i class="text-primary fas fa-plane-arrival" style="font-size:20px"></i>
          </div>

          <div class="col">
            <i class="fa-solid fa-location-dot">
              {{flight.arrival?.place}}
            </i>
          </div>

          <div class="col m-2">
            <i class="fa-regular"></i>
            {{flight.arrival?.time | date}}
          </div>

          <div class="col m-2">
            <i class="fa-regular fa-clock"></i>
            {{flight.arrival?.time | date:'shortTime'}}
          </div>
        </div>

      </div>
    </div>
  </div>
</div>
