import { Component } from '@angular/core';
import { FormBuilder, Validator, Validators } from '@angular/forms';
import { PassengerService } from './../api/services/passenger.service';
import { AuthService } from '../auth/auth.service';
import { Router, ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-register-passenger',
  templateUrl: './register-passenger.component.html',
  styleUrls: ['./register-passenger.component.css']
})
export class RegisterPassengerComponent {
  constructor(private passengerService: PassengerService,
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router,
    private activatedRoute: ActivatedRoute) { }

  returnUrl?: string = undefined;

  form = this.fb.group({
    email: ['', Validators.compose([Validators.required, Validators.minLength(3), Validators.maxLength(100)])],
    password: ['', Validators.compose([Validators.required, Validators.minLength(3), Validators.maxLength(35)])],
    name: ['', Validators.compose([Validators.required, Validators.minLength(3), Validators.maxLength(35)])],
    isFemale: [true, Validators.required]
  })


  ngOnInit(): void {
    this.activatedRoute.params.subscribe(p => this.returnUrl = p['returnUrl']);
  }

  checkPassenger(): void {
    const formEmail = this.form.get('email')?.value ?? "";
    const params = { email: formEmail };

    this.passengerService
      .findPassenger(params)
      .subscribe(this.login,
        e => {
          if (e.status != 404) {
            console.error(e);
          }
        });
  }

  register() {
    if (this.form.invalid)
      return;

    console.log("Form Values: ", this.form.value);

    this.passengerService.registerPassenger({ body: this.form.value })
      .subscribe(this.login, console.error);
  }

  private login = () => {
    this.authService.loginUser({ email: this.form.get('email')?.value ?? "" })
    this.router.navigate([ this.returnUrl ?? '/search-flights'])
  }
}
<h2 class="text-center mb-4">Register Passenger</h2>
<form [formGroup]="form">
  <div class="mb-3">
    <label for="inputEmail" class="form-label">Email address</label>
    <input (blur)="checkPassenger()" formControlName="email" type="email" class="form-control" id="inputEmail" aria-describedby="emailHelp">
    <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
    <app-custom-validator [formControl]="form.controls.email"></app-custom-validator>
  </div>
  <div class="mb-3">
    <label for="inputPassword" class="form-label">Password</label>
    <input type="password" formControlName="password" class="form-control" id="inputPassword">
    <app-custom-validator [formControl]="form.controls.password"></app-custom-validator>
  </div>
  <div class="mb-3">
    <label for="inputName" class="form-label">Name</label>
    <input type="text" formControlName="name" class="form-control" id="inputName">
    <app-custom-validator [formControl]="form.controls.name"></app-custom-validator>
  </div>
  <div class="mb-3 form-check">
    <input type="radio" formControlName="isFemale" class="form-check-input" id="inputIsMale" [value]="true">
    <label class="form-check-label" for="inputIsMale">Male</label>
  </div>
  <div class="mb-3 form-check">
    <input type="radio" formControlName="isFemale" class="form-check-input" id="inputIsFemale" [value]="false">
    <label class="form-check-label" for="inputIsFemale">Female</label>
  </div>
  <button (click)="register()" class="btn btn-primary">Submit</button>
</form>
