import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { PassengerService } from './../api/services/passenger.service';

@Component({
  selector: 'app-register-passenger',
  templateUrl: './register-passenger.component.html',
  styleUrls: ['./register-passenger.component.css']
})
export class RegisterPassengerComponent {
  constructor(private passengerService: PassengerService,
    private fb: FormBuilder) { }

  form = this.fb.group({
    email: [''],
    password: [''],
    name: [''],
    isFemale: [true]
  })


  ngOnInit(): void {

  }

  register() {
    console.log("Form Values: ", this.form.value);

    this.passengerService.registerPassenger({ body: this.form.value })
      .subscribe(_ => console.log("Form have been posted to the server."));
  }
}
<h2 class="text-center mb-4">Register Passenger</h2>
<form [formGroup]="form">
  <div class="mb-3">
    <label for="inputEmail" formControlName="email" class="form-label">Email address</label>
    <input type="email" class="form-control" id="inputEmail" aria-describedby="emailHelp">
    <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
  </div>
  <div class="mb-3">
    <label for="inputPassword"  formControlName="password" class="form-label">Password</label>
    <input type="password" class="form-control" id="inputPassword">
  </div>
  <div class="mb-3">
    <label for="inputName"  formControlName="name" class="form-label">Name</label>
    <input type="password" class="form-control" id="inputName">
  </div>
  <div class="mb-3 form-check">
    <input type="checkbox" formControlName="isFemale" class="form-check-input" id="inputIsMale" [value]="true">
    <label class="form-check-label" for="inputIsMale">Male</label>
  </div>
  <div class="mb-3 form-check">
    <input type="checkbox" formControlName="isFemale" class="form-check-input" id="inputIsFemale" [value]="false">
    <label class="form-check-label" for="inputIsFemale">Female</label>
  </div>
  <button (click)="register()" class="btn btn-primary">Submit</button>
</form>
